import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  LineChart, Line, BarChart, Bar, AreaChart, Area, ComposedChart,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';

const DashboardPlots = () => {
  const [plotData, setPlotData] = useState({
    assetsLiabilities: null,
    profitability: null,
    expenses: null,
    cashFlow: null,
    margins: null,
    leverage: null
  });
  const [loading, setLoading] = useState({
    assetsLiabilities: true,
    profitability: true,
    expenses: true,
    cashFlow: true,
    margins: true,
    leverage: true
  });
  const [errors, setErrors] = useState({});

  // Color palettes for consistent styling
  const colorPalette = {
    primary: ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8', '#82CA9D'],
    assets: ['#4CAF50', '#8BC34A', '#FF9800', '#F44336'],
    profitability: ['#2196F3', '#4CAF50', '#FF5722'],
    expenses: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF', '#FF9F40'],
    cashFlow: ['#00C49F', '#0088FE', '#FFBB28', '#FF8042'],
    margins: ['#8884D8', '#82CA9D', '#FF8042', '#0088FE', '#FF6B6B'],
    leverage: ['#FF6B6B', '#4ECDC4', '#45B7D1']
  };

  useEffect(() => {
    fetchAllPlotData();
  }, []);

  const processCSVData = (data) => {
    if (!data || !Array.isArray(data) || data.length === 0) return [];
    
    // Get all keys from the first data item (column headers)
    const allKeys = Object.keys(data[0]);
    
    // The first column contains the labels (like "Total Current Liabilities")
    // The rest are time periods
    const labelKey = allKeys[0]; // This is the label column
    const timePeriodKeys = allKeys.slice(1);
    
    if (timePeriodKeys.length === 0) return data;
    
    // Filter out columns where:
    // 1. The header is "--" OR
    // 2. ALL values in that column are "--" or "NA"
    const validTimePeriods = timePeriodKeys.filter(timePeriod => {
      // Remove if header is "--"
      if (timePeriod === '--') return false;
      
      // Remove if all values are "--" or "NA"
      const allInvalid = data.every(row => {
        const value = row[timePeriod];
        return value === '--' || value === 'NA' || value === '' || value === null || value === undefined;
      });
      
      return !allInvalid;
    });
    
    // Reverse the valid time periods to get chronological order
    const reversedTimePeriods = [...validTimePeriods].reverse();
    
    // Create new data with reversed time period columns
    const processedData = data.map(item => {
      const newItem = { [labelKey]: item[labelKey] }; // Keep the label
      
      // Map values to reversed time period keys
      reversedTimePeriods.forEach((newTimePeriod, index) => {
        const originalTimePeriod = validTimePeriods[index];
        newItem[newTimePeriod] = item[originalTimePeriod];
      });
      
      return newItem;
    });
    
    return processedData;
  };

  const fetchPlotData = async (plotName) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/user/plot/${plotName}`);
      
      if (response.data && Array.isArray(response.data.data)) {
        // Process the CSV data: remove "--" columns and reverse chronological order
        return processCSVData(response.data.data);
      } else {
        throw new Error('Invalid data format');
      }
    } catch (error) {
      console.warn(`Failed to fetch ${plotName} data, using dummy data:`, error);
      setErrors(prev => ({ ...prev, [plotName]: 'Using sample data' }));
      return generateDummyData(plotName);
    }
  };

  const fetchAllPlotData = async () => {
    try {
      const plotNames = ['01_plot_AandL.csv', '02_plot_revPL.csv', '03_plot_expenses.csv', '04_plot_cashflow.csv', '05_plot_margins.csv', '06_plot_leverage.csv'];
      
      const promises = plotNames.map(async (plotName) => {
        const data = await fetchPlotData(plotName);
        return { plotName, data };
      });

      const results = await Promise.all(promises);
      
      const newPlotData = {};
      const newLoading = { ...loading };
      
      results.forEach(({ plotName, data }) => {
        // Map the file names to our internal state keys
        let stateKey;
        switch(plotName) {
          case '01_plot_AandL':
            stateKey = 'assetsLiabilities';
            break;
          case '02_plot_revPL':
            stateKey = 'profitability';
            break;
          case '03_plot_expenses':
            stateKey = 'expenses';
            break;
          case '04_plot_cashflow':
            stateKey = 'cashFlow';
            break;
          case '05_plot_margins':
            stateKey = 'margins';
            break;
          case '06_plot_leverage':
            stateKey = 'leverage';
            break;
          default:
            stateKey = plotName;
        }
        newPlotData[stateKey] = data;
        newLoading[stateKey] = false;
      });
      
      setPlotData(newPlotData);
      setLoading(newLoading);
    } catch (error) {
      console.error('Error fetching plot data:', error);
    }
  };

  // Generate comprehensive dummy data for each plot type
  const generateDummyData = (plotType) => {
    switch (plotType) {
      case '01_plot_AandL':
        return [
          {
            'Total Current Liabilities': '1,189.20',
            'Mar 22': '1,189.20',
            'Mar 21': '1,521.30', 
            'Mar 20': '1,943.00',
            'Mar 19': '1,633.08',
            'Mar 18': '1,315.07'
          },
          {
            'Total Non-Current Liabilities': '3,108.30',
            'Mar 22': '3,108.30',
            'Mar 21': '3,006.40',
            'Mar 20': '4,212.80',
            'Mar 19': '2,911.85',
            'Mar 18': '2,803.50'
          },
          {
            'Total Current Assets': '3,455.20',
            'Mar 22': '3,455.20',
            'Mar 21': '3,037.70',
            'Mar 20': '2,315.00',
            'Mar 19': '2,003.02',
            'Mar 18': '1,979.83'
          },
          {
            'Total Non-Current Assets': '6,953.00',
            'Mar 22': '6,953.00',
            'Mar 21': '6,691.50',
            'Mar 20': '7,829.20',
            'Mar 19': '6,425.33',
            'Mar 18': '5,832.24'
          }
        ];

      case '02_plot_revPL':
        return [
          {
            'Total Revenue': '15,189.20',
            'Mar 22': '15,189.20',
            'Mar 21': '14,521.30',
            'Mar 20': '13,943.00',
            'Mar 19': '12,633.08',
            'Mar 18': '11,315.07'
          },
          {
            'Profit/Loss before Tax': '2,189.20',
            'Mar 22': '2,189.20',
            'Mar 21': '1,521.30',
            'Mar 20': '1,943.00',
            'Mar 19': '1,633.08',
            'Mar 18': '1,315.07'
          },
          {
            'Profit/Loss for the period': '1,889.20',
            'Mar 22': '1,889.20',
            'Mar 21': '1,321.30',
            'Mar 20': '1,643.00',
            'Mar 19': '1,433.08',
            'Mar 18': '1,215.07'
          }
        ];

      case '03_plot_expenses':
        const expenseCategories = ['Employee Benefits', 'Depreciation', 'Finance Costs', 'Other Expenses', 'Total Tax Expenses', 'Exceptional Items'];
        return expenseCategories.map(category => ({
          [category]: (Math.floor(Math.random() * 2000) + 500).toString(),
          'Mar 22': (Math.floor(Math.random() * 2000) + 500).toString(),
          'Mar 21': (Math.floor(Math.random() * 2000) + 500).toString(),
          'Mar 20': (Math.floor(Math.random() * 2000) + 500).toString(),
          'Mar 19': (Math.floor(Math.random() * 2000) + 500).toString(),
          'Mar 18': (Math.floor(Math.random() * 2000) + 500).toString()
        }));

      case '04_plot_cashflow':
        const cashFlowCategories = ['Cash Flow from Operating Activities', 'Cash Flow from Investing Activities', 'Cash Flow from Financing Activities', 'Net Increase in Cash and Cash Equivalents'];
        return cashFlowCategories.map(category => ({
          [category]: (Math.floor(Math.random() * 4000) - 1000).toString(),
          'Mar 22': (Math.floor(Math.random() * 4000) - 1000).toString(),
          'Mar 21': (Math.floor(Math.random() * 4000) - 1000).toString(),
          'Mar 20': (Math.floor(Math.random() * 4000) - 1000).toString(),
          'Mar 19': (Math.floor(Math.random() * 4000) - 1000).toString(),
          'Mar 18': (Math.floor(Math.random() * 4000) - 1000).toString()
        }));

      case '05_plot_margins':
        const marginMetrics = ['PBDIT Margin (%)', 'PBT Margin (%)', 'Net Profit Margin (%)', 'Return on Capital Employed (%)', 'Return on Assets (%)'];
        return marginMetrics.map(metric => ({
          [metric]: (Math.random() * 40 + 10).toFixed(2),
          'Mar 22': (Math.random() * 40 + 10).toFixed(2),
          'Mar 21': (Math.random() * 40 + 10).toFixed(2),
          'Mar 20': (Math.random() * 40 + 10).toFixed(2),
          'Mar 19': (Math.random() * 40 + 10).toFixed(2),
          'Mar 18': (Math.random() * 40 + 10).toFixed(2)
        }));

      case '06_plot_leverage':
        const leverageMetrics = ['Current Ratio (X)', 'Quick Ratio (X)', 'Total Debt/Equity (X)'];
        return leverageMetrics.map(metric => ({
          [metric]: (Math.random() * 2 + 0.5).toFixed(2),
          'Mar 22': (Math.random() * 2 + 0.5).toFixed(2),
          'Mar 21': (Math.random() * 2 + 0.5).toFixed(2),
          'Mar 20': (Math.random() * 2 + 0.5).toFixed(2),
          'Mar 19': (Math.random() * 2 + 0.5).toFixed(2),
          'Mar 18': (Math.random() * 2 + 0.5).toFixed(2)
        }));

      default:
        return [];
    }
  };

  // Transform data from backend format to chart format
  const transformDataForChart = (data) => {
    if (!data || data.length === 0) return [];
    
    // Get all time period keys (all keys except the first one which is the label)
    const timePeriods = Object.keys(data[0]).slice(1);
    
    if (timePeriods.length === 0) return [];
    
    // Transform: each time period becomes a data point with all metrics
    return timePeriods.map(timePeriod => {
      const dataPoint = { period: timePeriod };
      
      data.forEach(row => {
        const labelKey = Object.keys(row)[0]; // First key is the metric label
        const metricName = row[labelKey];
        let value = row[timePeriod];
        
        // Convert valid values to numbers, leave "--" and "NA" as null
        if (value && value !== '--' && value !== 'NA') {
          // Remove commas and convert to number
          const cleanValue = value.toString().replace(/,/g, '');
          const numericValue = parseFloat(cleanValue);
          dataPoint[metricName] = isNaN(numericValue) ? null : numericValue;
        } else {
          dataPoint[metricName] = null; // Missing values as null
        }
      });
      
      return dataPoint;
    });
  };

  // Dynamic key extractors for flexible data handling
  const getDynamicKeys = (data, excludeKeys = ['period']) => {
    if (!data || data.length === 0) return [];
    const sample = data[0];
    return Object.keys(sample).filter(key => !excludeKeys.includes(key));
  };

  // Format currency values in tooltips
  const formatCurrency = (value) => {
    if (value === null || value === undefined) return 'N/A';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(value);
  };

  // Format percentage values in tooltips
  const formatPercentage = (value) => {
    if (value === null || value === undefined) return 'N/A';
    return `${value.toFixed(1)}%`;
  };

  // Format ratio values in tooltips
  const formatRatio = (value) => {
    if (value === null || value === undefined) return 'N/A';
    return value.toFixed(2);
  };

  // Render individual plots with loading states
  const renderPlot = (plotKey, title, chartComponent, height = 300) => {
    const data = plotData[plotKey];
    const isLoading = loading[plotKey];
    const hasError = errors[plotKey];

    if (isLoading) {
      return (
        <div className="plot-card">
          <h3>{title}</h3>
          <div className="plot-loading">
            <div className="loading-spinner"></div>
            <p>Loading {title} data...</p>
          </div>
        </div>
      );
    }

    return (
      <div className="plot-card">
        <div className="plot-header">
          <h3>{title}</h3>
          {hasError && <span className="data-warning">Sample Data</span>}
        </div>
        {chartComponent}
      </div>
    );
  };

  // Plot 1: Stacked bar chart for assets and liabilities
  const renderAssetsLiabilitiesChart = () => {
    const rawData = plotData.assetsLiabilities || [];
    const data = transformDataForChart(rawData);
    
    return (
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="period" />
          <YAxis />
          <Tooltip formatter={formatCurrency} />
          <Legend />
          <Bar dataKey="Total Current Assets" stackId="assets" fill={colorPalette.assets[0]} name="Current Assets" />
          <Bar dataKey="Total Non-Current Assets" stackId="assets" fill={colorPalette.assets[1]} name="Non-Current Assets" />
          <Bar dataKey="Total Current Liabilities" stackId="liabilities" fill={colorPalette.assets[2]} name="Current Liabilities" />
          <Bar dataKey="Total Non-Current Liabilities" stackId="liabilities" fill={colorPalette.assets[3]} name="Non-Current Liabilities" />
        </BarChart>
      </ResponsiveContainer>
    );
  };

  // Plot 2: Trend line chart for profitability
  const renderProfitabilityChart = () => {
    const rawData = plotData.profitability || [];
    const data = transformDataForChart(rawData);
    
    return (
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="period" />
          <YAxis />
          <Tooltip formatter={formatCurrency} />
          <Legend />
          <Line 
            type="monotone" 
            dataKey="Total Revenue" 
            stroke={colorPalette.profitability[0]} 
            strokeWidth={2} 
            name="Total Revenue" 
            connectNulls={false}
          />
          <Line 
            type="monotone" 
            dataKey="Profit/Loss before Tax" 
            stroke={colorPalette.profitability[1]} 
            strokeWidth={2} 
            name="Profit before Tax" 
            connectNulls={false}
          />
          <Line 
            type="monotone" 
            dataKey="Profit/Loss for the period" 
            stroke={colorPalette.profitability[2]} 
            strokeWidth={2} 
            name="Net Profit/Loss" 
            connectNulls={false}
          />
        </LineChart>
      </ResponsiveContainer>
    );
  };

  // Plot 3: Stacked area chart of expenses (dynamic)
  const renderExpensesChart = () => {
    const rawData = plotData.expenses || [];
    const data = transformDataForChart(rawData);
    const expenseKeys = getDynamicKeys(data, ['period']);
    
    return (
      <ResponsiveContainer width="100%" height={300}>
        <AreaChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="period" />
          <YAxis />
          <Tooltip formatter={formatCurrency} />
          <Legend />
          {expenseKeys.map((key, index) => (
            <Area
              key={key}
              type="monotone"
              dataKey={key}
              stackId="1"
              stroke={colorPalette.expenses[index % colorPalette.expenses.length]}
              fill={colorPalette.expenses[index % colorPalette.expenses.length]}
              name={key}
              connectNulls={false}
            />
          ))}
        </AreaChart>
      </ResponsiveContainer>
    );
  };

  // Plot 4: Stacked area chart of cash flow (dynamic)
  const renderCashFlowChart = () => {
    const rawData = plotData.cashFlow || [];
    const data = transformDataForChart(rawData);
    const cashFlowKeys = getDynamicKeys(data, ['period']);
    
    return (
      <ResponsiveContainer width="100%" height={300}>
        <AreaChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="period" />
          <YAxis />
          <Tooltip formatter={formatCurrency} />
          <Legend />
          {cashFlowKeys.map((key, index) => (
            <Area
              key={key}
              type="monotone"
              dataKey={key}
              stackId="1"
              stroke={colorPalette.cashFlow[index % colorPalette.cashFlow.length]}
              fill={colorPalette.cashFlow[index % colorPalette.cashFlow.length]}
              name={key}
              connectNulls={false}
            />
          ))}
        </AreaChart>
      </ResponsiveContainer>
    );
  };

  // Plot 5: Trend line of margin ratios
  const renderMarginsChart = () => {
    const rawData = plotData.margins || [];
    const data = transformDataForChart(rawData);
    
    return (
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="period" />
          <YAxis />
          <Tooltip formatter={formatPercentage} />
          <Legend />
          <Line 
            type="monotone" 
            dataKey="PBDIT Margin (%)" 
            stroke={colorPalette.margins[0]} 
            strokeWidth={2} 
            name="PBDIT Margin" 
            connectNulls={false}
          />
          <Line 
            type="monotone" 
            dataKey="PBT Margin (%)" 
            stroke={colorPalette.margins[1]} 
            strokeWidth={2} 
            name="PBT Margin" 
            connectNulls={false}
          />
          <Line 
            type="monotone" 
            dataKey="Net Profit Margin (%)" 
            stroke={colorPalette.margins[2]} 
            strokeWidth={2} 
            name="Net Profit Margin" 
            connectNulls={false}
          />
          <Line 
            type="monotone" 
            dataKey="Return on Capital Employed (%)" 
            stroke={colorPalette.margins[3]} 
            strokeWidth={2} 
            name="ROCE" 
            connectNulls={false}
          />
          <Line 
            type="monotone" 
            dataKey="Return on Assets (%)" 
            stroke={colorPalette.margins[4]} 
            strokeWidth={2} 
            name="ROA" 
            connectNulls={false}
          />
        </LineChart>
      </ResponsiveContainer>
    );
  };

  // Plot 6: Trend line of leverage metrics
  const renderLeverageChart = () => {
    const rawData = plotData.leverage || [];
    const data = transformDataForChart(rawData);
    
    return (
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="period" />
          <YAxis />
          <Tooltip formatter={formatRatio} />
          <Legend />
          <Line 
            type="monotone" 
            dataKey="Current Ratio (X)" 
            stroke={colorPalette.leverage[0]} 
            strokeWidth={2} 
            name="Current Ratio" 
            connectNulls={false}
          />
          <Line 
            type="monotone" 
            dataKey="Quick Ratio (X)" 
            stroke={colorPalette.leverage[1]} 
            strokeWidth={2} 
            name="Quick Ratio" 
            connectNulls={false}
          />
          <Line 
            type="monotone" 
            dataKey="Total Debt/Equity (X)" 
            stroke={colorPalette.leverage[2]} 
            strokeWidth={2} 
            name="Debt/Equity" 
            connectNulls={false}
          />
        </LineChart>
      </ResponsiveContainer>
    );
  };

  return (
    <div className="dashboard-plots">
      <div className="plots-header">
        <h2>Financial Analytics Dashboard</h2>
        <button onClick={fetchAllPlotData} className="refresh-plots-btn">
          Refresh Data
        </button>
      </div>
      
      <div className="plots-grid">
        {renderPlot(
          'assetsLiabilities',
          'Assets & Liabilities Overview',
          renderAssetsLiabilitiesChart()
        )}
        
        {renderPlot(
          'profitability',
          'Profitability Trends',
          renderProfitabilityChart()
        )}
        
        {renderPlot(
          'expenses',
          'Expense Breakdown',
          renderExpensesChart()
        )}
        
        {renderPlot(
          'cashFlow',
          'Cash Flow Analysis',
          renderCashFlowChart()
        )}
        
        {renderPlot(
          'margins',
          'Margin & Return Ratios',
          renderMarginsChart()
        )}
        
        {renderPlot(
          'leverage',
          'Leverage & Liquidity Ratios',
          renderLeverageChart()
        )}
      </div>
    </div>
  );
};

export default DashboardPlots;
